public class p5_BasicOperators {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		int num1,num2, add, sub, mul, div, rem;
		num1=55;
		num2=10;
		add=num1+num2;
		sub=num1-num2;
		mul=num1*num2;
		div=num1/num2;
		rem=num1%num2;
		System.out.println("Value addition="+add);
		System.out.println("Value subtraction="+sub);
		System.out.println("Value multiply="+mul);
		System.out.println("Value divide="+div);
		System.out.println("Value remainder="+rem);
		

	}

}
