package assigments3BasedOnArraysAndLoops;

public class Tree4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		    int n=5;
	        for(int i=0; i<=n; i++)
	        {
	            for(int j=0; j<i; j++)
	            {
	                System.out.print(" ");
	            }
	         
	   	         for(int j=i; j<5; j++)
	   	        {
	   	       
	   	           System.out.print("* ");
	   	         }

	   	         System.out.println();
	   	       }   


	}

}
